mapper.select:
SELECT bd.ID bulletinID, bd.CompanyID companyID, bd.`Type` 'type', bd.`Name` 'name', bd.Content content, bd.CreateUser createUser, bd.UpdateTime updateTime, GROUP_CONCAT( bpr.Platform ) platformStr FROM tb_bulletin_data bd LEFT JOIN tb_bulletin_platform_relation bpr ON bd.ID = bpr.BulletinID WHERE bd.CompanyID=#{companyID}
AND bpr.Platform=#{platform}
AND bd.`Status`=#{status}
GROUP BY bd.ID
ORDER BY bd.`TopMost` DESC,bd.UpdateTime DESC,bd.`ID` DESC
ORDER BY bd.`TopMost` DESC,bd.CreateTime DESC,bd.`ID` DESC
LIMIT #{size}

SELECT bd.ID bulletinID, bd.CompanyID companyID, bd.`Status` 'status', bd.`Type` 'type', bd.`Name` 'name', bd.Content content, bd.CreateUser createUser, bd.CreateTime createTime, bd.UpdateTime updateTime, GROUP_CONCAT( bpr.Platform ) platformStr FROM tb_bulletin_data bd LEFT JOIN tb_bulletin_platform_relation bpr ON bd.ID = bpr.BulletinID WHERE bd.CompanyID=#{param.companyID}
AND (bd.`Name` LIKE CONCAT('%',#{param.queryCode},'%') OR bd.Content LIKE CONCAT('%',#{param.queryCode},'%'))
AND bpr.Platform=#{param.platform}
AND bd.`Type`=#{param.type}
AND bd.`Status`=#{param.status}
AND bd.UpdateTime >=#{param.startTime}
AND bd.UpdateTime <=#{param.endTime}
GROUP BY bd.ID
