mapper.select:
SELECT dwl.`ID` id, wtc.`WarnName` warnName, dwl.`WorkOrderID` workOrderID, dwl.`DealContent` dealContent, dwl.`DealUserID` dealUserID, dwl.`DealTime` dealTime, dwl.`DealStatus` dealStatus, dwl.`WarnTime` warnTime, dwl.`WarnEndTime` warnEndTime, pi.`ID` projectID, pi.`ProjectName` projectName, pi.`ShortName` projectShotName, mi.`ID` monitorItemID, mi.`Name` monitorItemName, mi.`Alias` monitorItemAlias, mp.`ID` monitorPointID, mp.`Name` monitorPointName, mp.`GpsLocation` gpsLocation, mtf.`ID` fieldID, mtf.`FieldName` fieldName, mtf.`FieldToken` fieldToken, s.`ID` sensorID, s.`Name` sensorName, s.`Alias` sensorAlias, dwl.`WarnLevel` warnLevel, wla.`Alias` alias FROM tb_data_warn_log dwl LEFT JOIN tb_warn_threshold_config wtc ON wtc.ID = dwl.WarnThresholdID LEFT JOIN tb_sensor s ON s.ID = wtc.SensorID LEFT JOIN tb_monitor_item mi ON mi.ID = wtc.MonitorItemID LEFT JOIN tb_monitor_point mp ON mp.ID = s.MonitorPointID AND mp.MonitorItemID = mi.ID LEFT JOIN tb_project_info pi ON pi.Id = mi.ProjectID LEFT JOIN tb_monitor_type_field mtf ON mtf.ID = wtc.FieldID LEFT JOIN tb_warn_level_alias wla ON wla.MonitorItemID = mi.ID AND wla.FieldID = mtf.ID AND wla.WarnLevel=dwl.WarnLevel AND wla.Platform =wtc.Platform WHERE dwl.CompanyID=#{param.companyID} AND dwl.Platform=#{param.platform}
AND dwl.`WarnEndTime` IS NULL
AND dwl.`WarnEndTime` IS NOT NULL
AND (mp.`Name` LIKE CONCAT('%',#{param.queryCode},'%') OR wtc.`WarnName` LIKE CONCAT('%',#{param.queryCode},'%'))
AND pi.ID =#{param.projectID}
AND dwl.WarnTime >= #{param.startTime}
AND dwl.WarnTime <= #{param.endTime}
AND dwl.DealStatus =#{param.dealStatus}
AND dwl.`WarnLevel` =#{param.warnLevel}
