mapper.update:
update tb_monitor_group
set ImagePath    = #{path},
    UpdateTime   = #{date},
    UpdateUserID = #{userID}
where ID = #{groupID}

mapper.select:
select Count(1)
from tb_monitor_group
where name = #{name}
  and projectID = #{projectID}

    and ParentID is not null


    and ParentID is null
