mapper.select:
select tmp.ID, tmp.ProjectID, tmp.MonitorType, tmp.MonitorItemID, tmp.`Name`, tmp.InstallLocation, tmp.GpsLocation, tmp.ImageLocation, tmp.OverallViewLocation, tmp.SpatialLocation, tmp.ExValues, tmp.DisplayOrder, tmp.CreateTime, tmp.CreateUserID, tmp.UpdateTime, tmp.UpdateUserID, tmi.Name as monitorItemName, tmi.Alias as monitorItemAlias, tpmc.Density from tb_monitor_point tmp LEFT JOIN tb_monitor_item tmi ON tmi.ID = tmp.MonitorItemID LEFT JOIN tb_project_monitor_class tpmc ON tmi.MonitorClass = tpmc.MonitorClass where tmp.ProjectID in
#{projectID}
and tmp.MonitorType = #{monitorType}
and tmp.MonitorItemID = #{monitorItemID}
and tmi.Name = #{monitorItemName}
and tmi.monitorClass = #{monitorClassType}
group by tmp.ID, tpmc.Density, tmi.Name;

select p.*, t.ID as monitorTypeID, t.TypeName as monitorTypeName, t.TypeAlias as monitorTypeAlias, t.MultiSensor as monitorTypeMultiSensor, it.Name as monitorItemName, it.Alias as monitorItemAlias from tb_monitor_point as p left join tb_monitor_type as t on t.MonitorType = p.MonitorType left join tb_monitor_item as it on it.id = p.MonitorItemID where p.ProjectID = #{projectID}
and p.MonitorType = #{monitorType}
and p.MonitorItemID = #{monitorItemID}
and ( p.Name like concat('%', #{queryCode}, '%') or p.id in (select distinct MonitorPointID from tb_sensor where Name like concat('%', #{queryCode}, '%') or Alias like concat('%', #{queryCode}, '%')) )
order by p.ID desc

select id, Name from tb_monitor_point where ProjectID = #{projectID}
and MonitorItemID in
#{iid}
and id in (select MonitorPointID from tb_monitor_group_point where MonitorGroupID = #{groupID})
order by id desc

