mapper.select:
select p.ID as monitorPointID , p.`Name` as monitorPointName , p.GpsLocation as pointGpsLocation , p.ImageLocation as pointImageLocation , p.MonitorItemID , p.MonitorType , i.`Name` as monitorItemName , i.Alias as monitorItemAlias , s.ID as sensorID , s.`Name` as sensorName , s.exValues , s.Status , pi.ID as projectID , pi.ProjectName as projectName , pi.ShortName as projectShortName , pi.Location , pi.ProjectAddress from tb_monitor_point as p left join tb_project_info as pi on pi.ID = p.ProjectID left join tb_monitor_item as i on i.ID = p.MonitorItemID left join tb_sensor as s on s.MonitorPointID = p.ID where p.ProjectID in
#{projectID}
and s.Status = #{onlineInt}
and s.Status = #{status}
and pi.Location LIKE CONCAT('%', #{areaCode}, '%')
and s.ExValues LIKE CONCAT('%', #{videoType}, '%')
and p.monitorItemID = #{monitorItemID}
and p.`ID` = #{monitorPointID}
and ( i.Name LIKE CONCAT('%', #{monitorItemName}, '%') or i.Alias LIKE CONCAT('%', #{monitorItemName}, '%') )
and i.MonitorType = #{monitorType}

select p.ID as monitorPointID , p.`Name` as monitorPointName , p.MonitorItemID , i.`Name` as monitorItemName , i.Alias as monitorItemAlias , p.ProjectID from tb_monitor_point as p left join tb_monitor_item as i on i.ID = p.MonitorItemID where p.ProjectID in
#{projectID}
and i.Alias = #{monitorItemName}

SELECT tmp.`ID` ID, tmp.`ProjectID` projectID, tmp.`MonitorType` monitorType, tmp.`Name` name, tmi.ID monitorItemID, tmi.`Name` monitorItemName, tmi.Alias monitorItemAlias FROM tb_monitor_point tmp LEFT JOIN tb_monitor_item tmi ON tmi.ID = tmp.MonitorItemID WHERE tmi.ID IS NOT NULL AND tmp.ProjectID IN
#{item}
AND tmp.MonitorType IN
#{item}

SELECT *
FROM tb_monitor_point tmp
         LEFT JOIN tb_monitor_item tmi ON tmi.ID = tmp.MonitorItemID
WHERE 1= 1
  AND tmi.ProjectID = #{projectID}
  AND tmi.MonitorClass = #{monitorClass}
