mapper.select:
select tnr.NotifyID notifyID,1 type,twtc.ProjectID,MonitorItemID monitorItemID,MonitorPointID monitorPointID,null historyFlag from tb_notify_relation tnr inner join tb_data_warn_log tdwl on tdwl.ID = tnr.RelationID inner join tb_warn_threshold_config twtc on twtc.ID=tdwl.WarnThresholdID and tnr.Type=1 inner join tb_sensor ts on ts.ID = twtc.SensorID where 1=1
and twtc.projectID = #{projectID}
union all select tnr.NotifyID notifyID,2 type,tdwl.ProjectID,null monitorItemID,null monitorPointID,IF(ISNULL(WarnEndTime),0,1) historyFlag from tb_notify_relation tnr inner join tb_device_warn_log tdwl on tdwl.ID=tnr.RelationID and tnr.Type=2 where 1=1
where tdwl.projectID = #{projectID}
