SELECT tpi.ProjectName projectName, tmp.MonitorType monitorType, ts.ID sensorID, IF(ts.`Status` IS NULL,-1,ts.`Status`) "status", tmt.TypeName monitorTypeName, t2.upperName FROM tb_project_info tpi LEFT JOIN tb_monitor_point tmp ON tpi.ID = tmp.ProjectID LEFT JOIN tb_monitor_type tmt ON tmt.MonitorType = tmp.MonitorType LEFT JOIN tb_sensor ts ON ts.MonitorPointID = tmp.ID LEFT JOIN ( SELECT TRIM( BOTH '"' FROM JSON_EXTRACT( twt.CompareRule, "$.upperName" ) ) upperName, tmp2.ID MonitorPointID FROM tb_warn_log twl LEFT JOIN tb_monitor_point tmp2 ON twl.MonitorPointID = tmp2.ID LEFT JOIN tb_sensor ts2 ON ts2.MonitorPointID = tmp2.ID LEFT JOIN tb_warn_trigger twt ON twt.ID = twl.triggerID LEFT JOIN ( SELECT MAX( WarnTime ) latest_time, MonitorPointID FROM tb_warn_log GROUP BY MonitorPointID ) twl1 ON twl.MonitorPointID = twl1.MonitorPointID WHERE twl1.latest_time = twl.WarnTime AND ts2.`Status` IN ( 1, 2, 3, 4 ) ) t2 ON t2.MonitorPointID = tmp.ID WHERE tpi.ID IN
#{item}
AND IF(tmp.ID IS NULL,1=1,tmp.CreateTime <= #{endTime})
