SELECT twl.`ID` warnID, twl.WarnType warnType, twl.`Name` warnName, twl.WarnLevel warnLevel, twl.WarnTime warnTime, twl.WarnContent warnContent, tpi.`ID` projectID, tpi.ProjectName projectName, tmi.`ID` monitorItemID, tmi.`Name` monitorItemName, tmp.`ID` monitorPointID, tmp.`Name` monitorPointName, tmt.MonitorType monitorTypeID, tmt.TypeName monitorTypeName, tmt.TypeAlias monitorTypeAlias, two.`ID` workOrderID, two.OrderCode orderCode, two.`Status` orderStatus FROM tb_warn_log twl LEFT JOIN tb_project_info tpi ON twl.ProjectID = tpi.ID LEFT JOIN tb_monitor_item tmi ON twl.MonitorItemID = tmi.ID LEFT JOIN tb_monitor_type tmt ON twl.MonitorType = tmt.MonitorType LEFT JOIN tb_monitor_point tmp ON twl.MonitorPointID = tmp.ID LEFT JOIN tb_work_order two ON twl.WorkOrderID = two.ID LEFT JOIN ( SELECT MAX( WarnTime ) latest_time, MonitorPointID FROM tb_warn_log WHERE WarnType = 1 GROUP BY MonitorPointID ) twl1 ON twl.MonitorPointID = twl1.MonitorPointID
twl1.latest_time > twl.WarnTime
twl1.latest_time = twl.WarnTime
AND twl.ProjectID IN
#{item}
AND twl.WarnType = 1
AND (twl.`Name` LIKE CONCAT('%', #{param.queryCode}, '%') OR twl.WarnContent LIKE CONCAT('%', #{param.queryCode}, '%'))
AND twl.monitorType=#{param.monitorTypeID}
AND twl.monitorItemID=#{param.monitorItemID}
AND twl.warnLevel=#{param.warnLevel}
ORDER BY twl.WarnTime DESC
ORDER BY twl.WarnTime ASC

SELECT twl.`ID` warnID, twl.WarnType warnType, twl.`Name` warnName, twl.WarnLevel warnLevel, twl.WarnTime warnTime, twl.WarnContent warnContent, twl.DeviceToken deviceToken, tpi.`ID` projectID, tpi.ProjectName projectName, tmi.`ID` monitorItemID, tmi.`Name` monitorItemName, tmp.`ID` monitorPointID, tmp.`Name` monitorPointName, tmt.MonitorType monitorTypeID, tmt.TypeName monitorTypeName, tmt.TypeAlias monitorTypeAlias, two.`ID` workOrderID, two.OrderCode orderCode, two.`Status` orderStatus FROM tb_warn_log twl LEFT JOIN tb_project_info tpi ON twl.ProjectID = tpi.ID LEFT JOIN tb_monitor_item tmi ON twl.MonitorItemID = tmi.ID LEFT JOIN tb_monitor_type tmt ON twl.MonitorType = tmt.MonitorType LEFT JOIN tb_monitor_point tmp ON twl.MonitorPointID = tmp.ID LEFT JOIN tb_work_order two ON twl.WorkOrderID = two.ID LEFT JOIN ( SELECT MAX( WarnTime ) latest_time, DeviceToken FROM `tb_warn_log` WHERE WarnType = 2 GROUP BY DeviceToken ) twl1 ON twl.DeviceToken = twl1.DeviceToken
twl1.latest_time > twl.WarnTime
twl1.latest_time = twl.WarnTime
AND twl.ProjectID IN
#{item}
AND twl.WarnType = 2
AND (twl.`Name` LIKE CONCAT('%', #{param.queryCode}, '%') OR twl.DeviceToken LIKE CONCAT('%', #{param.queryCode}, '%') OR tmp.`Name` LIKE CONCAT('%', #{param.queryCode}, '%') OR tpi.ProjectName LIKE CONCAT('%', #{param.queryCode}, '%'))
AND twl.monitorItemID=#{param.monitorItemID}
AND twl.WarnTime >= #{param.beginTime}
AND twl.WarnTime <= #{param.endTime}
AND two.`Status`=#{param.status}
ORDER BY twl.WarnTime DESC
ORDER BY twl.WarnTime ASC

SELECT twl.`ID` warnID, twl.WarnType warnType, twl.`Name` warnName, twl.WarnLevel warnLevel, twl.WarnTime warnTime, twl.WarnContent warnContent, twl.DeviceToken deviceToken, two.`ID` workOrderID, two.OrderCode orderCode, two.`Status` orderStatus FROM `tb_warn_log` twl LEFT JOIN `tb_work_order` two ON twl.WorkOrderID = two.ID LEFT JOIN (SELECT MAX(WarnTime) latest_time, DeviceToken FROM tb_warn_log WHERE WarnType = 3 GROUP BY DeviceToken) twl1 ON twl.DeviceToken = twl1.DeviceToken
twl1.latest_time > twl.WarnTime
twl1.latest_time = twl.WarnTime
AND twl.WarnType = 3
AND twl.warnLevel = #{param.warnLevel}
AND twl.WarnTime >= #{param.beginTime}
AND twl.WarnTime <= #{param.endTime}
AND two.`Status`=#{param.status}
ORDER BY twl.WarnTime DESC
ORDER BY twl.WarnTime ASC

