mapper.select:
SELECT twnc.`CompanyID`, twnc.`Platform`, twnc.`NotifyType`, twnc.`WarnLevel`, twnc.`NotifyMethod`, twnc.`Depts`, twnc.`Users`, twnc.`Roles`, twnc.`ExValue`, tncpr.ProjectID projectID FROM `tb_warn_notify_config` twnc LEFT JOIN `tb_notify_config_project_relation` tncpr ON twnc.ID = tncpr.NotifyConfigID
AND tncpr.ProjectID IN (#{projectID}, - 1)
AND twnc.`NotifyType` = #{notifyType}
AND twnc.`Platform` = #{platform}
ORDER BY tncpr.ProjectID DESC LIMIT 1

select twnc.ID, twnc.`CompanyID`, twnc.`Platform`, twnc.`NotifyType`, twnc.`WarnLevel`, twnc.`NotifyMethod`, twnc.`Depts`, twnc.`Users`, twnc.`Roles`, twnc.`ExValue` FROM `tb_warn_notify_config` twnc LEFT JOIN `tb_notify_config_project_relation` tncpr ON twnc.ID = tncpr.NotifyConfigID
twnc.CompanyID = #{companyID} and twnc.Platform = #{platform} and twnc.`NotifyType` = #{notifyType} and tncpr.ProjectID = #{projectID}
ORDER BY tncpr.ProjectID DESC LIMIT 1
