mapper.select:
SELECT twr.ID 'ID', twr.RuleType ruleType, twr.ProjectID projectID, twr.MonitorType monitorType, twr.MonitorItemID monitorItemID, twr.MonitorPointID monitorPointID, twr.SensorID sensorID, twr.ProductID productID, twr.DeviceCSV deviceCSV, twr.VideoType videoType, twr.VideoCSV videoCSV, twr.`Name` 'name', twr.`Enable` 'enable', twr.ExValue exValue, twr.`Desc` 'desc', twr.CreateTime createTime, twr.CreateUserID createUserID, twr.UpdateTime updateTime, twr.UpdateUserID updateUserID FROM tb_warn_rule twr LEFT JOIN ( SELECT twt.RuleID ruleID, GROUP_CONCAT( twt.`Name` ) warnNames FROM tb_warn_trigger twt GROUP BY twt.RuleID ) t ON t.ruleID = twr.ID WHERE twr.RuleType =#{param.ruleType}
AND twr.`ProjectID` IN
#{item}
AND twr.`Name` LIKE CONCAT('%',#{param.engineName},'%')
AND twr.`ProjectID`=#{param.projectID}
AND twr.VideoType=#{param.videoTypeName}
AND twr.`Enable`=#{param.enable}
AND twr.`MonitorItemID`=#{param.monitorItemID}
AND twr.`MonitorPointID`=#{param.monitorPointID}
AND (t.warnNames LIKE CONCAT('%',#{param.queryCode},'%') OR twr.ExValue LIKE CONCAT('%',#{param.queryCode},'%') OR twr.`Name` LIKE CONCAT('%',#{param.queryCode},'%'))
AND (t.warnNames LIKE CONCAT('%',#{param.queryCode},'%') OR twr.ExValue LIKE CONCAT('%',#{param.queryCode},'%') OR twr.`Name` LIKE CONCAT('%',#{param.queryCode},'%'))
