SELECT mp.`ID` monitorPointID, mp.`Name` monitorPointName, s.`ID` sensorID, s.`Name` sensorName, s.`Alias` sensorAlias, mtf.`ID` fieldID, mtf.`FieldName` fieldName, mtf.`FieldToken` fieldToken, wtc.`ID` configID, wtc.`WarnName` warnName, wtc.`CompareMode` compareMode, wtc.`Enable` wtcEnable, wtc.`Value` wtcValue FROM tb_monitor_point mp LEFT JOIN tb_sensor s ON s.`MonitorPointID` = mp.`ID`
AND mp.`ID` IN
#{item}
LEFT JOIN tb_monitor_item_field mif ON mif.`MonitorItemID` = mp.`MonitorItemID` LEFT JOIN tb_monitor_type_field mtf ON mtf.`ID` = mif.`MonitorTypeFieldID` LEFT JOIN tb_warn_threshold_config wtc ON wtc.`MonitorItemID` = mp.`MonitorItemID` AND wtc.`SensorID` = s.`ID` AND wtc.`FieldID` = mtf.`ID` AND wtc.`Platform`=#{param.platform}
AND wtc.`SensorID` IN
#{item}
WHERE s.`ID` IS NOT NULL AND mp.`MonitorItemID`=#{param.monitorItemID}
